<script>
export default {
  name: "VueChangeLanguage",
  methods: {
    changeLanguage() {
      const currentLocale = this.$i18n.locale;
      const newLocale = currentLocale === "en" ? "ru" : "en";
      this.$i18n.locale = newLocale;
    },
  },
};
</script>

<style lang="scss">
.lang_btn {
